import React, { useMemo, useState, CSSProperties } from "react";
import { Dialog, Button } from "react-desktop/macOs";
/// <reference path="react-desktop.d.ts" />

interface DialogProps {
  width: number;
  height: number;
  id: string;
  title: string;
  message: string;
  onCheck: (T: any) => void;
  onClose: (T: any) => void;
}

export const useDialog = () => {
  const [isVisible, setIsVisible] = useState(false);
  const showDialog = () => setIsVisible(true);
  const hideDialog = () => setIsVisible(false);
  const RenderDialog = ({
    width,
    height,
    id,
    title,
    message,
    onCheck,
    onClose,
  }: DialogProps) => {
    const styles = useMemo(
      () => ({
        width: `${width}px`,
        height: `${height}px`,
        left: `calc(50vw - ${width / 2}px)`,
        top: `calc(50vh - ${height}px)`,
        borderRadius: "4px",
      }),
      [width, height]
    );

    const renderIcon = () => {
      return (
        <img
          src="data:image/png;base64,"
          width="52"
          height="52"
        />
      );
    };
    return (
      <React.Fragment>
        {isVisible && (
          <div id={id} style={styles as CSSProperties}>
            <Dialog
              title={title}
              message={message}
              icon={renderIcon}
              buttons={[
                <Button onClick={onClose}>取消</Button>,
                <Button color="blue" onClick={onCheck}>
                  确认
                </Button>,
              ]}
            />
          </div>
        )}
      </React.Fragment>
    );
  };

  return {
    showDialog,
    hideDialog,
    RenderDialog,
  };
};
